function convertCurrency(g){var w=new Array("零","壹","贰","叁","肆","伍","陆","柒","捌","玖");var f=new Array("","拾","佰","仟");var x=new Array("","万","亿","兆");var e=new Array("角","分","毫","厘");var d="整";var a="元";var t=1000000000000000;var v;var b;var s="";var l;if(g==""){return""}g=parseFloat(g);if(g>=t){return""}if(g==0){s=w[0]+a+d;return s}g=g.toString();if(g.indexOf(".")==-1){v=g;b=""}else{l=g.split(".");v=l[0];b=l[1].substr(0,4)}if(parseInt(v,10)>0){var h=0;var y=v.length;for(var u=0;u<y;u++){var o=v.substr(u,1);var k=y-u-1;var j=k/4;var r=k%4;if(o=="0"){h++}else{if(h>0){s+=w[0]}h=0;s+=w[parseInt(o)]+f[r]}if(r==0&&h<4){s+=x[j]}}s+=a}if(b!=""){var c=b.length;for(var u=0;u<c;u++){var o=b.substr(u,1);if(o!="0"){s+=w[Number(o)]+e[u]}}}if(s==""){s+=w[0]+a+d}else{if(b==""){s+=d}}return s};